const items = $input.all();


const binary = {};

let attachmentFields = [];
let counter = 0;


for (const item of items) {
  const binaries = item.binary;

  for (const key in binaries) {
    const binaryData = binaries[key];
    const mimeType = binaryData.mimeType || '';

    if (mimeType.startsWith('image/')) {
      const fieldName = `file_${counter}`;
      binary[fieldName] = binaryData;
      attachmentFields.push(fieldName);
      counter++;
    }
  }
}

const inputFieldsString = attachmentFields.join(',');


return [
  {
    json: {
      input_fields: inputFieldsString,
      number_of_images: counter
    },
    binary
  }
];

const items = $input.all();

const results = items.map(item => {
  const versionString = $input.first().json.output.version || '';


  const parts = versionString.split(':');

  const versionId = parts.length > 1 ? parts[1] : '';

  return {
    json: {
      version_id: versionId
    }
  };
});

return results;

const items = $input.all();

let pdfFile = null;


for (const item of items) {
  const binaries = item.binary;

  for (const key in binaries) {
    const binaryData = binaries[key];
    const mimeType = binaryData.mimeType || '';

    if (mimeType === 'application/pdf') {
      pdfFile = binaryData;
      break;     }
  }

  if (pdfFile) {
    break; 
  }
}

if (pdfFile) {
  return [
    {
      json: {
        filename: pdfFile.fileName || 'product_description.pdf'
      },
      binary: {
        data: pdfFile
      }
    }
  ];
} else {
  throw new Error('No PDF file found among attachments.');
}

